mapper.sql:
where 1=1 

	
		and un.houseNum = #{extra.houseNum}
	
	
	
		and un.proprietorName = #{extra.name}
	
	
	
		and un.seq = #{extra.seq}
	

	
		
			and TO_DAYS(un.createTime) = TO_DAYS(NOW()) 
		
		
		
			and TO_DAYS(un.createTime) = TO_DAYS(#{extra.createTime}) 
		
	
	
	
		
			and un.status = #{extra.status}
