where 1=1

	
		AND #{extra.eventType} = fi.event_type
	
	
		AND fi.create_time between #{extra.startDate} And #{extra.endDate}
	
	
		AND #{extra.status} = fi.status
	
	
		AND fi.location like CONCAT('%','#{extra.location}','%' );
	
	
		AND fi.location like CONCAT('%','#{extra.location}','%' );
