mapper.sql:
where 1=1


		AND  #{extra.startDate}  <=  fi.start_date 
		AND  #{extra.endDate}	>= fi.end_date

		AND  #{extra.feeType}	= fi.fee_type


		AND fi.project_id = #{extra.projectId}



		AND fi.building_id = #{extra.buildingId}



		AND fi.house_num = #{extra.houseNum}



		AND fi.status = #{extra.status}



		AND fi.proprietor_name  like CONCAT('%','${extra.proprietorName}','%' )

mapper.select:
select fi.id, fi.house_num, fi.fee_type, fi.start_date, fi.end_date, fi.proprietor_name, fi.usage_amount, fi.unit_price, fi.total_fee, fi.deadline, fi.discount, fi.deadline, fi.late_fee_rate, fi.status, sum( (total_fee * discount)+ (total_fee *(case when datediff(fi.deadline,NOW())>0 then datediff(fi.deadline,NOW()) else 0 end) * fi.late_fee_rate)) as receivableAmount from t_fee_item fi
GROUP BY fi.project_id,fi.house_num
${pager.mysqlQueryCondition}
