mapper.sql:
where 1=1


        and cv.oemCode = #{extra.oemCode}



        AND cv.projectId = #{extra.projectId}



        AND cv.projectId in ${extra.projectIds}



        AND cv.status = ${extra.status}



        AND cv.userName like CONCAT('%',#{extra.userName},'%')

mapper.select:
SELECT * FROM () AS cv
ORDER BY cv.createTime DESC
