mapper.sql:
where 1=1


        and gd.oem_code = #{extra.oemCode}


        AND gd.project_id = #{extra.projectId}


        AND gd.project_id in ${extra.projectIds}


        AND gd.guard_type = #{extra.guardType}


        AND gd.guard_num = #{extra.guardNum}


        AND gd.guard_name like concat('%', #{extra.guardName},'%')


        AND dp.name like concat('%', #{extra.position},'%')


        AND d.status = #{extra.status}


        AND d.run_status = #{extra.runStatus}


    	AND d.type = #{extra.deviceType}

mapper.select:
SELECT gd.id, p.full_name AS projectName, gd.guard_num, d.name, d.type, dp.name positionName, gd.guard_num, d.run_status, d.status, gd.qrcode_url AS qrCodeUrl, sd.name AS deviceType FROM t_guard_device gd LEFT JOIN t_device_position dp ON gd.position_id = dp.id LEFT JOIN t_project p ON gd.project_id = p.id LEFT JOIN basic.t_device d ON gd.device_global_id = d.global_id LEFT JOIN property.t_sys_dict sd on sd.group_code = 'DEVICE_TYPE' AND sd.value = d.type
ORDER BY gd.project_id desc,gd.create_time desc
${pager.mysqlQueryCondition}

select p.full_name projectName, dp.name positionName, gd.*
from t_guard_device gd
LEFT JOIN t_device_position dp ON gd.position_id = dp.id LEFT JOIN t_project p ON gd.project_id = p.id where 1=1 AND gd.id = #{extra.deviceId}
