mapper.sql:
where 1=1


        and gd.oem_code = #{extra.oemCode}


        AND gd.project_id = #{extra.projectId}


        AND gd.project_id in ${extra.projectIds}


        AND gd.guard_type = #{extra.guardType}


        AND gd.guard_num = #{extra.guardNum}


        AND d.status = #{extra.status}


        AND d.run_status = #{extra.runStatus}

mapper.select:
SELECT gd.id, p.full_name project_id, gd.guard_num, d. NAME, d.type, dp. NAME positionName, gd.guard_num, d.run_status, d.`status` FROM t_guard_device gd LEFT JOIN t_device_position dp ON gd.position_id = dp.id LEFT JOIN t_project p ON gd.project_id = p.id LEFT JOIN basic.t_device d ON gd.device_global_id = d.global_id
ORDER BY gd.project_id desc,gd.create_time desc
${pager.mysqlQueryCondition}
