mapper.sql:
where 1=1 


	    AND a.parent_id = #{extra.parentId}



	    AND a.oem_code = #{extra.oemCode}



	    AND a.project_id =  #{extra.projectId}

	order by a.sortindex desc

mapper.select:
SELECT a.id, a.parent_id AS parentId, a.itemname, a.sortindex, a.project_id AS projectId, ( SELECT count(b.id) FROM t_house_check b WHERE a.oem_code = b.oem_code AND( ( a.parent_id != '0' AND a.parent_id = b.item_id AND a.id = b.result_id ) OR( a.parent_id = '0' AND a.id = b.item_id ) ) )AS count
FROM t_house_checkitem_config a
${pager.mysqlQueryCondition}
