mapper.sql:
where 1=1 


	    and h.oem_code = #{extra.oemCode}



	    AND h.project_id =  #{extra.projectId}



	    AND h.building_id = #{extra.buildingId}



	    AND h.house_num = #{extra.houseNum}



	    AND ph.pay_type = #{extra.payType}



	    AND p.name like CONCAT('%', #{extra.name },'%')

mapper.select:
SELECT h.id, h.house_num, h.project_id, building_id, floor_id, house_type, actual_area, gross_area, usable_area, apportion_area, fs.price as property_fee, sale_status, ownership, pay_type, name, p.id as proprietorId, ph.use_type as useType FROM property.t_house h left join property.t_proprietor_house ph on h.house_num = ph.house_num and h.project_id = ph.project_id left join property.t_fee_setting fs on fs.project_id = ph.project_id and fs.use_type = ph.use_type and fs.fee_type = '4' left join property.t_proprietor p on ph.proprietor_id = p.id
ORDER BY h.register_time DESC
${pager.mysqlQueryCondition}

