mapper.sql:
where 1=1 


	    and h.oem_code = #{extra.oemCode}



	    AND h.project_id =  #{extra.projectId}



	    AND h.building_id = #{extra.buildingId}



	    AND h.house_num = #{extra.houseNum}



	    AND ph.pay_type = #{extra.payType}



	    AND p.name like CONCAT('%', #{extra.name },'%')

mapper.select:
SELECT COUNT(1) FROM ( SELECT
h.id,h.house_num,h.project_id,building_id,floor_id,house_type, actual_area,gross_area,usable_area,apportion_area, property_fee,sale_status,ownership, ph.pay_type, p.name FROM property.t_house h left join property.t_proprietor_house ph on h.house_num = ph.house_num left join property.t_proprietor p on ph.proprietor_id = p.id
) t
