mapper.sql:
where 1=1 


	    and hs.oem_code = #{extra.oemCode}



	    AND hs.project_id =  #{extra.projectId}



	    AND hs.building_id = #{extra.buildingId}

mapper.select:
select hs.id as id, hs.project_id as projectId, p.full_name as projectName, hs.building_id as buildingId, pb.name as buildingName, pb.code as buildingCode, hs.num as num, hs.house_type as houseType, hs.actual_area as actualArea, hs.gross_area as grossArea, hs.usable_area as usableArea, hs.apportion_area as apportionArea, hs.property_fee as propertyFee, hs.ownership as ownership, hs.oem_code as oemCode, hs.floor_id as floorId, pb2.name as floorName from property.t_house_spec hs left join property.t_project p on hs.project_id = p.id left join property.t_project_building pb on hs.building_id = pb.id left join property.t_project_building pb2 on hs.floor_id = pb2.id where 1=1
and hs.oem_code = #{extra.oemCode}
AND hs.id = #{extra.id}
