mapper.sql:
where 
	t.id = p.message_id 
and t.type =  #{extra.type}
and(
	t.parent_id = ''
	or t.parent_id IS NULL
)
and(
	(
		p.type = '1'
		and p.group_id = '-1'
		and FIND_IN_SET(p.project_id, #{extra.manageProject})
	)
	or(
		p.type = '1'
		and p.group_id = #{extra.deptid}
		and p.recipient_id = '-1'
		and FIND_IN_SET(p.project_id, #{extra.manageProject})
	)
	or(
		p.type = '1'
		and p.group_id = #{extra.deptid}
		and p.recipient_id = #{extra.staffId}
		and FIND_IN_SET(p.project_id, #{extra.manageProject})
	)
	or t.creator_id = #{extra.staffId}
)
order by t.create_time desc
