mapper.sql:
where 1=1


		AND p.num = #{extra.num}


		 AND p.customer_name like CONCAT('%', #{extra.customerName },'%')


		AND pr.status = #{extra.status}

mapper.select:
select p.id, p.project_id, p.building_id, p.code, p.floor, p.num, p.area, p.suitable_car, p.pre_rent_price, p.pre_sale_price, p.manage_fee, p.status,
pr.id as parkingRentalId, pr.type, pr.house_num, pr.customer_name, pr.phone, pr.plate_num, pr.start_date, pr.end_date, pr.price, pr.authorization from t_parking p left join t_parking_rental pr on pr.parking_id=p.id
${pager.mysqlQueryCondition}
