mapper.sql:
WHERE 1=1 
and stf.status = '1'
and stf.creator_id != '0'


        and stf.oem_code = #{extra.oemCode}



        and stf.department_id = #{extra.departmentId}



        and stf.work_status = #{extra.workStatus}



        and ( stf.number = #{extra.idOrName} or stf.name like concat('%',#{extra.idOrName},'%') )

mapper.select:
SELECT stf.id, stf.number, stf.`name`, cst.id_num, stf.entry_time, stfarc.phone, dept.`name` department, dict.`name` position, stf.work_status, stf.account FROM t_property_staff stf LEFT JOIN t_staff_archive stfarc ON stf.id = stfarc.staff_id LEFT JOIN basic.t_customer cst ON stfarc.cust_global_id = cst.global_id LEFT JOIN t_property_department dept on stf.department_id = dept.id LEFT JOIN t_sys_dict dict on stf.position = dict.`value` and dict.group_code = 'POSITION'
LEFT JOIN t_manage_project mp on stf.id = mp.staff_id and mp.project_id = #{extra.projectId}
order by stf.create_time desc
${pager.mysqlQueryCondition}

select stf.* , pd.name as departmentName, sd.name as positionName from t_property_staff stf
left join t_manage_project mp on stf.id = mp.staff_id
left join property.t_property_department pd on pd.id = stf.department_id left join t_sys_dict sd on sd.`value`=stf.position and sd.group_code ='POSITION' where 1=1
AND mp.project_id = #{extra.projectId}
AND stf.department_id = #{extra.departmentId}

SELECT ts.*, pd.name as departmentName, sd.name as positionName FROM t_property_department pd LEFT JOIN t_property_staff ts ON pd.director_id = ts.id LEFT JOIN t_manage_project mp ON ts.id = mp.staff_id left join t_sys_dict sd on sd.`value`=ts.position and sd.group_code ='POSITION' WHERE 1=1
AND mp.project_id = #{projectId}
AND pd.id= #{departmentId}
AND ts.id IS NOT NULL GROUP BY ts.id

SELECT c.client_id AS clientId FROM t_property_staff s, t_manage_project p, t_staff_msg_client c WHERE s.id = p.staff_id AND s.id = c.staff_id AND s. STATUS = '1' AND s.work_status = '1'
AND p.project_id = #{extra.projectId}
AND s.department_id = #{extra.departmentId}

SELECT s.id, s.name, s.number, a.phone, a.cust_global_id, c.client_id FROM t_property_staff s, t_staff_archive a, t_staff_msg_client c WHERE s.work_status = '1' AND s. STATUS = '1' AND s.id = a.staff_id AND s.id = c.staff_id
AND s.department_id = #{extra.departmentId}
AND c.oem_code = #{extra.oemCode }
