mapper.sql:
	WHERE 1=1 

	
	    AND a.proprietor_id =  #{extra.proprietorId}
	
	
	
	    AND b.name like concat('%', #{extra.proprietorName}, '%')
	

	
	    AND a.id =  #{extra.proprietorHouseId}
	
	
	
	    AND a.project_id =  #{extra.projectId}
	
	
	
		AND a.project_id IN  ${extra.projectIds}
	
	
	
	    AND c.full_name =  #{extra.projectName}
	
	
	
	    AND c.floor_id =  #{extra.floorId}
	
	
	
	    AND c.building_id = #{extra.buildingId}
	
	
	
	    AND a.house_num like  concat('%', #{extra.houseNum},'%')
	
	
	
	    AND b.phone like concat('%', #{extra.phone}, '%')
	
	
	
	    AND a.oem_code =  #{extra.oemCode}

	WHERE 1=1 

	
	    AND v.projectId =  #{extra.projectId}
	
	
	
	    AND v.oemCode = #{extra.oemCode}
	
	
	
	    AND v.buildingId = #{extra.buildingId}
	
	
	
	    AND v.floorId = #{extra.floorId}
	
	
	
	    AND v.mobile LIKE CONCAT('%',#{extra.phone},'%')
	
	
	
	    AND c.real_name LIKE CONCAT('%',#{extra.proprietorName},'%')
	
	
	
	    AND v.houseNum LIKE CONCAT('%',#{extra.houseNum},'%')

mapper.select:
SELECT b.project_id AS projectId, b.id AS proprietorId, e.full_name AS projectName, b.name AS name, b.phone AS phone, a.id AS proprietorHouseId, a.use_type AS useType, a.pay_type AS payType, a.checkin_date AS checkinDate, c.id As houseId, c.house_num AS houseNum, d.id_num AS idNum FROM property.t_proprietor_house a LEFT JOIN property.t_proprietor b ON a.proprietor_id = b.id LEFT JOIN property.t_house c ON a.project_id = c.project_id AND a.house_num = c.house_num LEFT JOIN basic.t_customer d ON b.cust_global_id = d.global_id LEFT JOIN property.t_project e ON e.id = a.project_id
ORDER BY a.register_time DESC
${pager.mysqlQueryCondition}

SELECT b.id AS proprietorId, b.project_id AS projectId, e.full_name AS projectName, b.name AS name, b.phone AS phone, b.email AS email, b.work_unit AS workUnit, b.address AS address, b.zip_code AS zipCode, b.emergency_contact AS emergencyContact, b.emergency_phone AS emergencyPhone, b.pic_url AS picUrl, b.remark AS remark,
a.id AS proprietorHouseId, a.house_num as houseNum, a.contract_code AS contractCode, a.use_type AS useType, a.pay_type AS payType, a.checkin_mode AS checkinMode, a.checkin_date AS checkinDate, a.mandatary_relation AS mandataryRelation, a.mandatary_name AS mandataryName, a.mandatary_sex AS mandatarySex, a.mandatary_phone AS mandataryPhone, a.mandatary_id_num AS mandataryIdNum, a.mandatary_work_unit AS mandataryWorkUnit, a.mandatary_address AS mandataryAddress,
c.id AS houseId, d.id AS customerId, d.global_id AS custGlobalId, d.id_num AS idNum, d.mobile AS mobile, d.real_name AS realName, d.sex AS sex, d.birthday AS birthday, d.marital_status AS maritalStatus, d.status AS status FROM property.t_proprietor_house a LEFT JOIN property.t_proprietor b ON a.proprietor_id = b.id LEFT JOIN property.t_house c ON a.project_id = c.project_id AND a.house_num = c.house_num LEFT JOIN basic.t_customer d ON b.cust_global_id = d.global_id LEFT JOIN property.t_project e ON e.id = a.project_id WHERE a.id = #{proprietorHouseId}

SELECT DISTINCT v.id, v.userGlobalId, v.custGlobalId, v.registerType, v.mobile, v.activateTime, v.lastLoginTime, v.status, v.oemCode, c.real_name AS customerName FROM ( () UNION ALL () ) AS v LEFT JOIN basic.t_customer c ON c.global_id = v.custGlobalId
ORDER BY v.createTime DESC
${pager.mysqlQueryCondition}

( SELECT c.client_id as clientId, c.user_id as userId FROM ( SELECT DISTINCT u.id FROM basic.t_user u, property.t_proprietor p, property.t_proprietor_house t, property.t_house h WHERE u.cust_global_id = p.cust_global_id
AND u.oem_code = #{extra.oemCode}
AND p.id = t.proprietor_id AND t.project_id = h.project_id AND t.house_num = h.house_num
AND h.project_id = #{extra.projectId}
AND h.building_id = #{extra.buildingId}
)s, basic.t_push_msg_client c WHERE s.id = c.user_id ) UNION ( SELECT c.client_id as clientId, c.user_id as userId FROM ( SELECT DISTINCT u.id FROM basic.t_user u, property.t_resident r, property.t_resident_house t, property.t_house h WHERE u.cust_global_id = r.cust_global_id
AND u.oem_code = #{extra.oemCode}
AND r.id = t.resident_id AND t.house_id = h.id
AND h.project_id = #{extra.projectId}
AND h.building_id = #{extra.buildingId}
)s, basic.t_push_msg_client c WHERE s.id = c.user_id )

( SELECT DISTINCT c.client_id AS clientId, c.user_id AS userId FROM basic.t_user u, property.t_proprietor p, basic.t_push_msg_client c WHERE u.cust_global_id = p.cust_global_id
AND u.oem_code = #{extra.oemCode}
AND u.id = c.user_id
AND p.project_id = #{extra.projectId}
) UNION ( SELECT DISTINCT c.client_id AS clientId, c.user_id AS userId FROM basic.t_user u, property.t_resident r, basic.t_push_msg_client c WHERE u.cust_global_id = r.cust_global_id
AND u.oem_code = #{extra.oemCode}
AND u.id = c.user_id
AND r.project_id = #{extra.projectId}
)

( SELECT DISTINCT c.client_id AS clientId, c.user_id AS userId FROM basic.t_user u, property.t_proprietor p, property.t_proprietor_house h, basic.t_push_msg_client c WHERE u.cust_global_id = p.cust_global_id
AND u.oem_code = #{extra.oemCode}
AND u.id = c.user_id AND p.id = h.proprietor_id
AND h.project_id = #{extra.projectId}
AND h.house_num = #{extra.houseNum}
) UNION ( SELECT DISTINCT c.client_id AS clientId, c.user_id AS userId FROM basic.t_user u, property.t_resident r, property.t_resident_house t, property.t_house h, basic.t_push_msg_client c WHERE u.cust_global_id = r.cust_global_id
AND u.oem_code = #{extra.oemCode}
AND u.id = c.user_id AND r.id = t.resident_id AND t.house_id = h.id
AND h.project_id = #{extra.projectId}
AND h.house_num = #{extra.houseNum}
)

SELECT p.id AS proprietorId, p.cust_global_id AS custGlobalId, p. NAME AS NAME, p.phone AS phone, p.email AS email, p.work_unit AS workUnit, p.address AS address, p.zip_code AS zipCode, p.emergency_contact AS emergencyContact, p.emergency_phone AS emergencyPhone, p.pic_url AS picUrl FROM t_proprietor p, t_proprietor_house h WHERE p.id = h.proprietor_id
AND h.project_id = #{extra.projectId}
AND h.house_num = #{extra.houseNum}
AND h.oem_code = #{extra.oemCode}
