mapper.sql:
WHERE 1=1 


    AND rh.proprietor_id =  #{extra.proprietorId}



    AND rh.house_id =  #{extra.houseId}



    AND r.id =  #{extra.residentId}



    AND r.oem_code =  #{extra.oemCode}



    AND r.custGlobalId =  #{extra.custGlobalId}



    AND r.status =  #{extra.status}

mapper.select:
SELECT r.id as id, r.cust_global_id as custGlobalId, r.project_id as projectId, r.name as name, r.mobile as mobile, r.tel as tel, r.work_unit as workUnit, r.hobbies as hobbies, r.specialty as specialty, r.pic as pic, r.remark as remark, r.oem_code as oemCode,
rh.id as residentHouseId, rh.house_id as houseId, rh.proprietor_id as proprietorId, rh.resident_type as residentType, rh.resident_relation as residentRelation, rh.checkin_date as checkinDate, rh.checkup_date as checkupDate,
c.id as customerId, c.global_id as globalId, c.id_num as idNum, c.sex as sex, c.birthday as birthday, c.native_place as nativePlace, c.national as national, c.marital_status as maritalStatus, c.edu_degree as eduDegree FROM property.t_resident r LEFT JOIN property.t_resident_house rh ON rh.resident_id = r.id AND rh.oem_code = r.oem_code LEFT JOIN basic.t_customer c ON r.cust_global_id = c.global_id WHERE r.id = #{extra.residentId} AND rh.house_id = #{extra.houseId}

mapper.sql:
    WHERE 1=1 


	        AND t.projectId =  #{extra.projectId}



	        AND t.houseNum =  #{extra.houseNum}



	        AND t.name like CONCAT('%',#{extra.name},'%')



	        AND t.building_id =  #{extra.buildingId}

mapper.select:
select * from ( SELECT p.id projectId, p.full_name projectName, h.building_id, h.house_num houseNum, r.id, rh.resident_relation residentRelation, r.`name`, r.mobile, gc.id guardCardId, gc.sn guardCardSn, gc.status guardCardStatus FROM t_resident r LEFT JOIN t_resident_house rh on r.id = rh.resident_id LEFT JOIN t_house h ON rh.house_id = h.id LEFT JOIN t_project p ON h.project_id = p.id LEFT JOIN t_guard_card_user gcu ON r.id = gcu.user_id LEFT JOIN t_guard_card gc ON gcu.card_id = gc.id where 1=1
AND r.oem_code = #{extra.oemCode}
UNION
SELECT prj.id projectId, prj.full_name projectName, h.building_id, ph.house_num houseNum, p.id, '01' residentRelation, p.`name`, p.phone mobile, gc.id guardCardId, gc.sn guardCardSn, gc.status guardCardStatus FROM property.t_proprietor p LEFT JOIN property.t_proprietor_house ph ON p.id = ph.proprietor_id LEFT JOIN property.t_project prj ON ph.project_id = prj.id LEFT JOIN property.t_guard_card_user gcu ON p.id = gcu.user_id LEFT JOIN property.t_guard_card gc ON gcu.card_id = gc.id LEFT JOIN property.t_house h on prj.id = ph.project_id and ph.house_num = h.house_num where 1=1
AND p.oem_code = #{extra.oemCode}
) t
${pager.mysqlQueryCondition}

select count(*) from ( SELECT p.id projectId, p.full_name projectName, h.building_id, h.house_num houseNum, r.id, rh.resident_relation residentRelation, r.`name`, r.mobile, gc.id guardCardId, gc.sn guardCardSn, gc.status guardCardStatus FROM t_resident r LEFT JOIN t_resident_house rh on r.id = rh.resident_id LEFT JOIN t_house h ON rh.house_id = h.id LEFT JOIN t_project p ON h.project_id = p.id LEFT JOIN t_guard_card_user gcu ON r.id = gcu.user_id LEFT JOIN t_guard_card gc ON gcu.card_id = gc.id where 1=1
AND r.oem_code = #{extra.oemCode}
UNION
SELECT prj.id projectId, prj.full_name projectName, h.building_id, ph.house_num houseNum, p.id, '01' residentRelation, p.`name`, p.phone mobile, gc.id guardCardId, gc.sn guardCardSn, gc.status guardCardStatus FROM property.t_proprietor p LEFT JOIN property.t_proprietor_house ph ON p.id = ph.proprietor_id LEFT JOIN property.t_project prj ON ph.project_id = prj.id LEFT JOIN property.t_guard_card_user gcu ON p.id = gcu.user_id LEFT JOIN property.t_guard_card gc ON gcu.card_id = gc.id LEFT JOIN property.t_house h on prj.id = ph.project_id and ph.house_num = h.house_num where 1=1
AND p.oem_code = #{extra.oemCode}
) t
