mapper.sql:
WHERE 1=1 


    AND rh.proprietor_id =  #{extra.proprietorId}



    AND rh.house_id =  #{extra.houseId}



    AND r.id =  #{extra.residentId}



    AND r.oem_code =  #{extra.oemCode}



    AND r.custGlobalId =  #{extra.custGlobalId}

mapper.select:
SELECT r.id as id, r.cust_global_id as custGlobalId, r.project_id as projectId, r.name as name, r.mobile as mobile, r.tel as tel, r.work_unit as workUnit, r.hobbies as hobbies, r.specialty as specialty, r.pic as pic, r.remark as remark, r.oem_code as oemCode,
rh.id as residentHouseId, rh.house_id as houseId, rh.proprietor_id as proprietorId, rh.resident_type as residentType, rh.resident_relation as residentRelation, rh.checkin_date as checkinDate, rh.checkup_date as checkupDate,
c.id as customerId, c.global_id as globalId, c.id_num as idNum, c.sex as sex, c.birthday as birthday, c.native_place as nativePlace, c.national as national, c.marital_status as maritalStatus, c.edu_degree as eduDegree FROM property.t_resident r LEFT JOIN property.t_resident_house rh ON rh.resident_id = r.id AND rh.oem_code = r.oem_code LEFT JOIN basic.t_customer c ON r.cust_global_id = c.global_id WHERE r.id = #{extra.residentId} AND rh.house_id = #{extra.houseId}
