mapper.sql:
WHERE 1=1 


    AND a.proprietor_id =  #{extra.proprietorId}



    AND a.house_id =  #{extra.houseId}



    AND a.id =  #{extra.residentId}



    AND a.oem_code =  #{extra.oemCode}



    AND a.custGlobalId =  #{extra.custGlobalId}

WHERE 1=1 


    AND t.projectId =  #{extra.projectId}



    AND t.houseNum =  #{extra.houseNum}



    AND t.name like CONCAT('%',#{extra.name},'%')



    AND t.building_id =  #{extra.buildingId}

mapper.select:
select * from ( SELECT p.id projectId, p.full_name projectName, h.building_id, h.house_num houseNum, r.id, r.resident_relation residentRelation, r.`name`, r.mobile, gc.id guardCardId, gc.sn guardCardSn, gc.status guardCardStatus, rc.plate_num plateNum FROM t_resident r LEFT JOIN t_house h ON r.house_id = h.id LEFT JOIN t_project p ON h.project_id = p.id LEFT JOIN t_guard_card_user gcu ON r.id = gcu.user_id LEFT JOIN t_guard_card gc ON gcu.card_id = gc.id LEFT JOIN t_resident_car rc on h.id = rc.house_id where 1=1
AND r.oem_code = #{extra.oemCode}
UNION
SELECT prj.id projectId, prj.full_name projectName, h.building_id, ph.house_num houseNum, p.id, '01' residentRelation, p.`name`, p.phone mobile, gc.id guardCardId, gc.sn guardCardSn, gc.status guardCardStatus, rc.plate_num plateNum FROM t_proprietor p LEFT JOIN t_proprietor_house ph ON p.id = ph.proprietor_id LEFT JOIN t_project prj ON ph.project_id = prj.id LEFT JOIN t_guard_card_user gcu ON p.id = gcu.user_id LEFT JOIN t_guard_card gc ON gcu.card_id = gc.id LEFT JOIN t_house h on prj.id = ph.project_id and ph.house_num = h.house_num LEFT JOIN t_resident_car rc on h.id = rc.house_id where 1=1
AND p.oem_code = #{extra.oemCode}
) t
${pager.mysqlQueryCondition}

select count(*) from ( SELECT p.id projectId, p.full_name projectName, h.building_id, h.house_num houseNum, r.id, r.resident_relation residentRelation, r.`name`, r.mobile, gc.id guardCardId, gc.sn guardCardSn, gc.status guardCardStatus, rc.plate_num plateNum FROM t_resident r LEFT JOIN t_house h ON r.house_id = h.id LEFT JOIN t_project p ON h.project_id = p.id LEFT JOIN t_guard_card_user gcu ON r.id = gcu.user_id LEFT JOIN t_guard_card gc ON gcu.card_id = gc.id LEFT JOIN t_resident_car rc on h.id = rc.house_id where 1=1
AND r.oem_code = #{extra.oemCode}
UNION
SELECT prj.id projectId, prj.full_name projectName, h.building_id, ph.house_num houseNum, p.id, '01' residentRelation, p.`name`, p.phone mobile, gc.id guardCardId, gc.sn guardCardSn, gc.status guardCardStatus, rc.plate_num plateNum FROM t_proprietor p LEFT JOIN t_proprietor_house ph ON p.id = ph.proprietor_id LEFT JOIN t_project prj ON ph.project_id = prj.id LEFT JOIN t_guard_card_user gcu ON p.id = gcu.user_id LEFT JOIN t_guard_card gc ON gcu.card_id = gc.id LEFT JOIN t_house h on prj.id = ph.project_id and ph.house_num = h.house_num LEFT JOIN t_resident_car rc on h.id = rc.house_id where 1=1
AND p.oem_code = #{extra.oemCode}
) t
