mapper.select:
SELECT t.id, t.cust_global_id globalId, t.project_id projectId, t.`name`, t.phone mobile, t.`status`, t.pic_url pic, t.oem_code oemCode, t.household_type householdType, t.resident_relation residentRelation FROM ( SELECT p.id, p.cust_global_id, p.project_id, p.`name`, p.phone, p.`status`, p.pic_url, p.oem_code, "1" household_type, "01" resident_relation FROM t_proprietor p UNION SELECT r.id, r.cust_global_id, r.project_id, r.`name`, r.mobile, r.`status`, r.pic, r.oem_code, "2" household_type, rh.resident_relation FROM t_resident r left join t_resident_house rh on r.id = rh.resident_id ) t where 1=1 AND t.id = #{loginId}
AND v.householdType = '1'
AND v.householdType = '2'
