mapper.sql:
WHERE 1=1 


    AND a.proprietor_id =  #{extra.proprietorId}



    AND a.id =  #{extra.proprietorHouseId}



    AND a.project_id =  #{extra.projectId}



    AND c.short_name =  #{extra.projectName}



    AND c.floor_id =  #{extra.floorId}



    AND c.building_id = #{extra.buildingId}



    AND a.house_num = #{extra.houseNum}



    AND b.phone = #{extra.phone}

mapper.select:
SELECT b.project_id AS projectId, e.short_name AS projectName, b.name AS name, b.phone AS phone, a.id AS proprietorHouseId, a.use_type AS useType, a.pay_type AS payType, a.checkin_date AS checkinDate, c.id As houseId, c.house_num AS houseNum, d.id_num AS idNum FROM property.t_proprietor_house a LEFT JOIN property.t_proprietor b ON a.proprietor_id = b.id LEFT JOIN property.t_house c ON a.project_id = c.project_id AND a.house_num = c.house_num LEFT JOIN basic.t_customer d ON b.cust_global_id = d.global_id LEFT JOIN property.t_project e ON e.id = a.project_id
ORDER BY a.register_time DESC
${pager.mysqlQueryCondition}
